const express = require("express");
const bodyParser = require("body-parser");
const axios = require("axios");
require("dotenv").config();

const app = express();
const port = process.env.PORT || 3000;

app.use(bodyParser.json());

// Serve static files (HTML, CSS, JS)
app.use(express.static("text-generator"));

// POST endpoint to handle AI text generation
app.post("/generate-text", async (req, res) => {
    const prompt = req.body.prompt;

    if (!prompt) {
        return res.status(400).json({ error: "No prompt provided" });
    }

    try {
        // Make a request to OpenAI's API using the API key
        const response = await axios.post("https://api.openai.com/v1/completions", {
            model: "text-davinci-003", // You can change the model to "gpt-4" if you have access
            prompt: prompt,
            max_tokens: 100, // Adjust token limit as needed
            temperature: 0.7, // Adjust randomness
        }, {
            headers: {
                Authorization: `Bearer ${process.env.OPENAI_API_KEY}`,
            },
        });

        // Return the generated text from OpenAI
        const text = response.data.choices[0].text.trim();
        res.json({ text });
    } catch (error) {
        console.error("OpenAI API error:", error);
        res.status(500).json({ error: "Failed to generate text" });
    }
});

// Start the server
app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
});
